#[cfg(test)]
mod tests {
    use whisky_csl::{calculate_tx_hash, sign_transaction};

    #[test]
    fn test_calculate_tx_hash() {
        let tx_hex = "";
        let tx_hash = calculate_tx_hash(tx_hex).unwrap();
        assert_eq!(
            tx_hash,
            "174ed92a1c005606b8d814b8e921a8f03666ffb16cfcb82459770f918ddafc25"
        );
    }

    #[test]
    fn test_cli_signed_tx() {
        let tx_hex: &str = "";
        let signed_tx = sign_transaction(
            tx_hex,
            &["51022b7e38be01d1cc581230e18030e6e1a3e949a1fdd2aeae5f5412154fe82b"],
        )
        .unwrap();
        assert_eq!(
            signed_tx,
            ""
        );
    }
}
