#[test]
fn parser_test() {
    let utxo_1: UTxO = serde_json::from_str("{\"input\":{\"outputIndex\":8,\"txHash\":\"2c255d39a6d448b408bdb1734c99dfc8c487ac23fd7ee5e8b431a99bc514980a\"},\"output\":{\"address\":\"addr_test1zptl0h0ceq3d4tgrlkqgyv2n5cwez0juj9rm63uw8nxhpv6u5ayjvx4rk9a29n2tqf4uv4nvfv2yy8tqs0kuue8luh9sczalnn\",\"amount\":[{\"quantity\":\"5000000\",\"unit\":\"lovelace\"}],\"dataHash\":\"923918e403bf43c34b4ef6b48eb2ee04babed17320d8d1b9ff9ad086e86f44ec\",\"plutusData\":\"d87980\",\"scriptHash\":null,\"scriptRef\":null}}").unwrap();
    let utxo_2: UTxO = serde_json::from_str("{\"input\":{\"outputIndex\":3,\"txHash\":\"ffb4e04fd430ffd1bdf014990c6d63a5303c1745ff228b70823fc757a04b1c64\"},\"output\":{\"address\":\"addr_test1zptl0h0ceq3d4tgrlkqgyv2n5cwez0juj9rm63uw8nxhpv6u5ayjvx4rk9a29n2tqf4uv4nvfv2yy8tqs0kuue8luh9sczalnn\",\"amount\":[{\"quantity\":\"15000000\",\"unit\":\"lovelace\"},{\"quantity\":\"50000000\",\"unit\":\"5066154a102ee037390c5236f78db23239b49c5748d3d349f3ccf04b55534458\"}],\"dataHash\":\"923918e403bf43c34b4ef6b48eb2ee04babed17320d8d1b9ff9ad086e86f44ec\",\"plutusData\":\"d87980\",\"scriptHash\":null,\"scriptRef\":null}}").unwrap();
    let utxo_3: UTxO = serde_json::from_str("{\"input\":{\"outputIndex\":4,\"txHash\":\"40e1afc8b735a9daf665926554b0e11902e3ed7e4a31a23b917483d4de42c05e\"},\"output\":{\"address\":\"addr_test1zptl0h0ceq3d4tgrlkqgyv2n5cwez0juj9rm63uw8nxhpv6u5ayjvx4rk9a29n2tqf4uv4nvfv2yy8tqs0kuue8luh9sczalnn\",\"amount\":[{\"quantity\":\"1500000\",\"unit\":\"lovelace\"},{\"quantity\":\"50000000\",\"unit\":\"5066154a102ee037390c5236f78db23239b49c5748d3d349f3ccf04b55534458\"}],\"dataHash\":\"923918e403bf43c34b4ef6b48eb2ee04babed17320d8d1b9ff9ad086e86f44ec\",\"plutusData\":\"d87980\",\"scriptHash\":null,\"scriptRef\":null}}").unwrap();
    let utxo_4: UTxO = serde_json::from_str("{\"input\":{\"outputIndex\":2,\"txHash\":\"ffb4e04fd430ffd1bdf014990c6d63a5303c1745ff228b70823fc757a04b1c64\"},\"output\":{\"address\":\"addr_test1zptl0h0ceq3d4tgrlkqgyv2n5cwez0juj9rm63uw8nxhpv6u5ayjvx4rk9a29n2tqf4uv4nvfv2yy8tqs0kuue8luh9sczalnn\",\"amount\":[{\"quantity\":\"1500000\",\"unit\":\"lovelace\"},{\"quantity\":\"250000000\",\"unit\":\"5066154a102ee037390c5236f78db23239b49c5748d3d349f3ccf04b55534458\"}],\"dataHash\":\"923918e403bf43c34b4ef6b48eb2ee04babed17320d8d1b9ff9ad086e86f44ec\",\"plutusData\":\"d87980\",\"scriptHash\":null,\"scriptRef\":null}}").unwrap();
    let utxo_5: UTxO = serde_json::from_str("{\"input\":{\"outputIndex\":0,\"txHash\":\"efe6fbbdd6b993d96883b96c572bfcaa0a4a138c83bd948dec1751d1bfda09b3\"},\"output\":{\"address\":\"addr_test1zqjmsmh2sjjy508e3068pck6lgp23k2msypgc52cxcgzjlju5ayjvx4rk9a29n2tqf4uv4nvfv2yy8tqs0kuue8luh9s5cdt49\",\"amount\":[{\"quantity\":\"1909330\",\"unit\":\"lovelace\"},{\"quantity\":\"1\",\"unit\":\"e6e5285a878161c101a59b4e36f1f99e5e464d30f510be3ee34f907f\"}],\"dataHash\":\"f15c78b61720654e3ed4d373e120a74cfd8816897f796f03b944e8dbb3c58523\",\"plutusData\":\"\",\"scriptHash\":null,\"scriptRef\":null}}").unwrap();
    let utxo_6: UTxO = serde_json::from_str("{\"input\":{\"outputIndex\":0,\"txHash\":\"ac7744adce4f25027f1ca009f5cab1d0858753e62c6081a3a3676cfd5333bb03\"},\"output\":{\"address\":\"addr_test1zptl0h0ceq3d4tgrlkqgyv2n5cwez0juj9rm63uw8nxhpv6u5ayjvx4rk9a29n2tqf4uv4nvfv2yy8tqs0kuue8luh9sczalnn\",\"amount\":[{\"quantity\":\"12684330\",\"unit\":\"lovelace\"}],\"dataHash\":null,\"plutusData\":null,\"scriptHash\":\"57f7ddf8c822daad03fd80823153a61d913e5c9147bd478e3ccd70b3\",\"scriptRef\":\"\"}}").unwrap();
    let utxo_7: UTxO = serde_json::from_str("{\"input\":{\"outputIndex\":7,\"txHash\":\"3fbdf2b0b4213855dd9b87f7c94a50cf352ba6edfdded85ecb22cf9ceb75f814\"},\"output\":{\"address\":\"addr_test1vpw22xesfv0hnkfw4k5vtrz386tfgkxu6f7wfadug7prl7s6gt89x\",\"amount\":[{\"quantity\":\"10000000\",\"unit\":\"lovelace\"}],\"dataHash\":null,\"plutusData\":null,\"scriptHash\":null,\"scriptRef\":null}}").unwrap();

    let utxos = vec![utxo_1, utxo_2, utxo_3, utxo_4, utxo_5, utxo_6, utxo_7];
    let tx_hex = "";
    let body = &parse(tx_hex, &utxos).unwrap();
    println!("parsed tx body: {:#?}", body);
    assert_eq!(body.inputs.len(), 4);
    assert_eq!(body.outputs.len(), 4);
    assert_eq!(body.collaterals.len(), 1);
    assert_eq!(body.required_signatures.len(), 2);
    assert_eq!(body.fee, None);
    assert_eq!(body.reference_inputs.len(), 2);
}

#[test]
fn test_withdraw_tx_decode() {
    let utxo_1: UTxO = serde_json::from_str("{\"input\":{\"outputIndex\":0,\"txHash\":\"66e9f787106bf68431827fc3cde3db92705e9ca984d404516a2c8014b30c8142\"},\"output\":{\"address\":\"addr_test1qpvx0sacufuypa2k4sngk7q40zc5c4npl337uusdh64kv0uafhxhu32dys6pvn6wlw8dav6cmp4pmtv7cc3yel9uu0nq93swx9\",\"amount\":[{\"quantity\":\"100000000\",\"unit\":\"lovelace\"}],\"dataHash\":null,\"plutusData\":null,\"scriptHash\":null,\"scriptRef\":null}}").unwrap();
    let utxos = vec![utxo_1];
    let tx_hex = "";
    let result = parse(tx_hex, &utxos).unwrap();
    println!("parsed withdraw tx body: {:#?}", &result);
    assert_eq!(result.inputs.len(), 1);
    assert_eq!(result.withdrawals.len(), 1);
    assert_eq!(result.withdrawals.get(0), Some(&Withdrawal::PlutusScriptWithdrawal(PlutusScriptWithdrawal {
        address: "stake_test17qeatpq2kx0ulnlkpshl2zw4xugjfmsuyec2hktdh8neqeq23t54a".to_string(),
        coin: 0,
        script_source: Some(
            whisky_common::ScriptSource::ProvidedScriptSource(ProvidedScriptSource {
                script_cbor: "583658340101002332259800a518a4d153300249011856616c696461746f722072657475726e65642066616c736500136564004ae715cd01".to_string(),
                language_version: LanguageVersion::V3,
            }),
        ),
        redeemer: Some(
            Redeemer {
                data: "40".to_string(),
                ex_units: Budget {
                    mem: 7000000,
                    steps: 3000000000,
                },
            },
        ),
    })));
}

#[test]
fn stake_credential_test() {
    let mut bytes =
        hex::decode("122a7d7854b9d9f42d067bac7295a8e02887afd8f25da48833f78d10").unwrap();
    bytes.insert(0, 0b11100001);
    let reward_account = RewardAccount::from_bytes(&bytes).unwrap();
    println!("reward account: {:#?}", &reward_account.to_bech32());
}

#[test]
fn drep_test() {
    let drep_bech32 = "drep1yfjez5zup0gystdvc933w2mn8k64hcy3krvc2namluwjxdcfhm8wd";
    let drep = DRep::from_bech32(drep_bech32).unwrap();
    println!("{:#?}", &drep.to_bech32_cip129().unwrap());
}

#[test]
fn test_check_required_signers() {
    let result = check_tx_required_signers("").unwrap();
    assert!(result);
}

#[test]
fn test_check_required_signers_2() {
    let mut result = check_tx_required_signers("").unwrap();
    assert!(!result);
}
