    #[test]
    fn test_script_tx_in_with_script_value() {
        let mut mesh = MeshTxBuilder::new_core();
        let asset = Asset {
            unit: "lovelace".to_string(),
            quantity: "30000000".to_string(),
        };

        let data = to_string(&json!({
            "constructor": 0,
            "fields": []
        }))
        .unwrap();

        let script_cbor = "";
        mesh.spending_plutus_script_v2()
            .tx_in(
                "93fec6deaafabcc394a15552b57b1beca120d9ee90480d1e5cb42ff20118d40a",
                1,
                vec![asset],
                "addr_test1vr3vljjxan0hl6u28fle2l4ds6ugc9t08lwevpauk38t3agx7rtq6",
            )
            .tx_in_script(script_cbor, LanguageVersion::V2)
            .tx_in_datum_value(&data)
            .spending_reference_tx_in_redeemer_value(Redeemer {
                data: data.clone(),
                ex_units: Budget {
                    mem: 3386819,
                    steps: 1048170931,
                },
            });
    }

